mapper.select:
SELECT /*selectFile */ fd.file_id, fd.real_file_name, fd.file_name, fd.reg_dtm, fd.web_path, fd.file_no, fd.phy_path FROM file_mst fm join file_dtl fd on fm.file_id = fd.file_id WHERE fm.file_id = #{fileId}
AND fm.file_no = #{fileNo}
ORDER BY file_no ASC LIMIT 1

SELECT /*selectFiles */
    (CASE WHEN ISNULL(max(file_no)) THEN '0'
        ELSE max(file_no) END
    ) as file_no 
FROM file_dtl 
where file_id = #{fileId}

mapper.insert:
INSERT /*insertFileMst */ INTO file_dtl ( file_id, real_file_name, file_name, reg_dtm, web_path, file_no, phy_path, reg_id, reg_ip ) SELECT A.file_id, A.real_file_name, A.file_name, A.reg_dtm, A.web_path, A.file_no, A.phy_path, A.reg_id, A.reg_ip FROM(
SELECT #{list.fileId} AS file_id, #{list.realFileName} AS real_file_name, #{list.fileName} AS file_name, sysdate() AS reg_dtm, #{list.webPath} AS web_path, #{list.fileNo} AS file_no, #{list.phyPath} phy_path, #{list.regId} AS reg_id, #{list.regIp} AS reg_ip
) A
